data Nat = Zero | Succ Nat deriving(Eq,Show)

-- 3A
intToNat :: Int -> Nat
intToNat i
    | i == 0    = Zero
    | i < 0     = (Succ (intToNat (abs(i)-1) ) )
    | otherwise = (Succ (intToNat (i-1)      ) )

-- 3B
natToInt :: Nat -> Int
natToInt Zero = 0
natToInt (Succ x) = 1 + natToInt x

-- 3C
soma :: Nat -> Nat -> Nat
soma a b = intToNat((natToInt a) + (natToInt b))

-- 3D
mult :: Nat -> Nat -> Nat
mult a b = intToNat((natToInt a) * (natToInt b))

